import Logo from "../../Images/Logo1.png";
import { Navbar, Button, Card, Form } from "react-bootstrap";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate from react-router-dom

// Define the type for a project
interface Project {
  name: string;
  id: number;
}

function Manager() {
  const navigate = useNavigate();
  const [projects, setProjects] = useState<Project[]>([]); // Specify the type as Project[]
  const [newProjectName, setNewProjectName] = useState(""); // State for new project name

  const handleLogout = () => {
    navigate("/");
  };

  const createProject = () => {
    if (newProjectName.trim() !== "") {
      // Create a new project and add it to the projects array
      const newProject: Project = {
        name: newProjectName,
        id: Date.now(),
      };

      setProjects([...projects, newProject]);
      setNewProjectName(""); // Clear the input field
    }
  };

  const deleteProject = (projectId: number) => {
    // Filter out the project with the specified ID
    const updatedProjects = projects.filter((project) => project.id !== projectId);
    setProjects(updatedProjects);
  };

  return (
    <div>
      <Navbar>
        <img src={Logo} alt="Logo" className="logo-1" />
        <Button
          variant="transparent"
          className="logout-btn"
          onClick={handleLogout}
        >
          Logout
        </Button>
      </Navbar>
      <div className="sidebar">
        <ul>
          <li>Home</li>
          <li>Services</li>
          <li>Options</li>
          <li>About Us</li>
          <li>Contact Us</li>
        </ul>
      </div>
      <div className="content">
        <h2>Create Project</h2>
        <Form>
          <Form.Group controlId="projectName">
            <Form.Control
              type="text"
              placeholder="Enter project name"
              value={newProjectName}
              onChange={(e) => setNewProjectName(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" onClick={createProject}>
            Create
          </Button>
        </Form>

        <h2>Projects</h2>
        <div className="project-cards">
          {projects.map((project) => (
            <Card key={project.id} className="project-card">
              <Card.Body>
                <Card.Title>{project.name}</Card.Title>
                <Button variant="info">Edit</Button>
                <Button variant="danger" onClick={() => deleteProject(project.id)}>
                  Delete
                </Button>
              </Card.Body>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Manager;
