// Signup.js
import React, { useState } from "react";
import { Container, Paper, Avatar, Typography, TextField, Button, MenuItem } from "@mui/material";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles((theme) => ({
  container: {
    background: "url('background-image.jpg')", // Replace with your background image
    backgroundSize: "cover",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  paper: {
    padding: theme.spacing(4),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    background: "rgba(255, 255, 255, 0.8)",
    borderRadius: "8px",
    backdropFilter: "blur(10px)", // Add a subtle blurry effect
  },
  avatar: {
    backgroundColor: theme.palette.primary.main,
  },
}));

const SignupPage = () => {
  const classes = useStyles();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleSignup = () => {
    // Handle signup logic here
  };

  return (
    <Container className={classes.container} maxWidth="sm">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography variant="h5">Sign Up</Typography>
        <TextField
          label="Email"
          fullWidth
          margin="normal"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <TextField
          label="Password"
          fullWidth
          margin="normal"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <TextField
          label="Role"
          fullWidth
          margin="normal"
          select
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <MenuItem value="Manager">Manager</MenuItem>
          <MenuItem value="Developer">Developer</MenuItem>
          <MenuItem value="QA">QA</MenuItem>
        </TextField>
        <Button
          variant="contained"
          color="primary"
          fullWidth
          onClick={handleSignup}
          style={{ marginTop: "20px" }}
        >
          Sign Up
        </Button>
      </Paper>
    </Container>
  );
};

export default SignupPage;
