import React, { useState } from "react";
import { Navbar, Button, Table } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Logo from "../../Images/Logo1.png";
import FileUpload from "./FileUpload";

function Manager() {
  const navigate = useNavigate();
  const [uploadedImageUrl, setUploadedImageUrl] = useState<string | null>(
    null
  ); // Specify the type for uploadedImageUrl
  const [projects, setProjects] = useState<string[]>([]); // Specify the type for projects as an array of strings

  const handleLogout = () => {
    navigate("/");
  };

  // Function to add a new project to the list
  const addProject = (projectName: string) => {
    setProjects([...projects, projectName]);
  };

  return (
    <div>
      <Navbar>
        <img src={Logo} alt="Logo" className="logo-1" />
        <Button
          variant="transparent"
          className="logout-btn"
          onClick={handleLogout}
        >
          Logout
        </Button>
      </Navbar>

      <div className="sidebar">
        <ul>
          <li>Home</li>
          <li>Services</li>
          <li>Options</li>
          <li>About Us</li>
          <li>Contact Us</li>
        </ul>
      </div>

      <div className="projects-table">
        <h2>Projects</h2>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th className="d-flex justify-content-between align-items-center">
                Project Name
                <FileUpload onUpload={addProject} />{" "}
                {/* Pass the addProject function */}
              </th>
            </tr>
          </thead>
          <tbody>
            {projects.map((projectName, index) => (
              <tr key={index}>
                <td>
                  {projectName}
                  <Button
                    variant="transparent"
                    size="sm"
                    className="buttons ml-2"
                  >
                    Assign Project
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default Manager;
