import React, { useState } from "react";
import { Navbar, Button, Table, Modal } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Logo from "../../Images/Logo1.png";
import FileUpload from "./FileUpload";

function Manager() {
  const navigate = useNavigate();
  const [uploadedImageUrl, setUploadedImageUrl] = useState<string | null>(null);
  const [projects, setProjects] = useState<string[]>([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(""); // State to store the selected project

  const handleLogout = () => {
    navigate("/");
  };

  const addProject = (projectName: string) => {
    setProjects([...projects, projectName]);
  };

  const openModal = (projectName: string) => {
    setSelectedProject(projectName);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      <Navbar>
        <img src={Logo} alt="Logo" className="logo-1" />
        <Button
          variant="transparent"
          className="logout-btn"
          onClick={handleLogout}
        >
          Logout
        </Button>
      </Navbar>

      <div className="sidebar">
        <ul>
          <li>Home</li>
          <li>Services</li>
          <li>Options</li>
          <li>About Us</li>
          <li>Contact Us</li>
        </ul>
      </div>

      <div className="projects-table">
        <h2>Projects</h2>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th className="d-flex justify-content-between align-items-center">
                Project Name
                <FileUpload onUpload={addProject} />
              </th>
            </tr>
          </thead>
          <tbody>
            {projects.map((projectName, index) => (
              <tr key={index}>
                <td
                  className="d-flex justify-content-between align-items-center"
                  onClick={() => openModal(projectName)} // Open the modal with the project name
                >
                  {projectName}
                  <Button
                    variant="dark"
                    size="sm"
                    className="ml-auto"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent modal from opening when clicking the button
                      openModal(projectName);
                    }}
                  >
                    Assign
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
      <Modal show={showModal} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedProject} Description</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Add the project description here */}
          {/* You can also add the Edit and Delete buttons here */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Close
          </Button>
          <Button variant="dark" onClick={closeModal}>
            Assign
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Manager;
