import Logo from "../../Images/Logo1.png";
import { Navbar, Button, Table } from "react-bootstrap";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Manager() {
  const navigate = useNavigate();
  const [selectedFile, setSelectedFile] = useState(null);

  const handleLogout = () => {
    navigate("/");
  };

  const handleFileChange = (e: { target: { files: any[]; }; }) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleAddProject = () => {
    if (selectedFile) {
      // Simulate uploading the file to the server (replace with actual server code)
      // You should use an actual server and Multer/Cloudinary for file uploads.
      setTimeout(() => {
        const uploadedFileName = selectedFile.name;
        // Update the <td> element with the file name (replace with your logic)
        const tdElement = document.getElementById("projectName");
        if (tdElement) {
          tdElement.textContent = uploadedFileName;
        }
      }, 2000); // Simulate a delay for server upload
    }
  };

  return (
    <div>
      <Navbar>
        <img src={Logo} alt="Logo" className="logo-1" />
        <Button
          variant="transparent"
          className="logout-btn"
          onClick={handleLogout}
        >
          Logout
        </Button>
      </Navbar>

      <div className="sidebar">
        <ul>
          <li>Home</li>
          <li>Services</li>
          <li>Options</li>
          <li>About Us</li>
          <li>Contact Us</li>
        </ul>
      </div>

      <div className="projects-table">
        <h2>Projects</h2>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th className="d-flex justify-content-between align-items-center">
                Project Name
                <input
                  type="file"
                  accept=".pdf, .doc, .docx" // Specify allowed file types
                  onChange={handleFileChange}
                />
                <Button
                  variant="transparent"
                  size="sm"
                  className="buttons"
                  onClick={handleAddProject}
                >
                  Add Project
                </Button>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td id="projectName">Project 1</td>
            </tr>
            <tr>
              <td>Project 2</td>
            </tr>
          </tbody>
        </Table>
      </div>
    </div>
  );
}

export default Manager;
