import React, { useState } from "react";
import axios from "axios";
import { Button, Modal } from "react-bootstrap"; // Import Modal from react-bootstrap

interface FileUploadProps {
  onUpload: (url: string) => void;
}

function FileUpload({ onUpload }: FileUploadProps) {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [showModal, setShowModal] = useState<boolean>(false); // State to control the modal visibility

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      console.error("No file selected for upload");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("my_file", selectedFile);

      const response = await axios.post("/upload", formData);

      if (response.data.secure_url) {
        onUpload(response.data.secure_url);
      }
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleAddProjectClick = () => {
    setShowModal(true); // Show the modal when the button is clicked
  };

  const handleCloseModal = () => {
    setShowModal(false); // Close the modal when needed
  };

  return (
    <div>
      <Button
        variant="transparent"
        size="sm"
        className="buttons"
        onClick={handleAddProjectClick} // Open the modal on button click
      >
        Add Project
      </Button>

      {/* Define the modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Hello World</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* You can add any content you want here */}
          {/* For example, your form for adding a project */}
          {/* Or any other content */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default FileUpload;
