import React, { useState } from 'react';

function Login() {
  const [isSignUp, setIsSignUp] = useState(true);

  const handleSignUpClick = () => {
    setIsSignUp(true);
  };

  const handleSignInClick = () => {
    setIsSignUp(false);
  };

  return (
    <div className={`container ${isSignUp ? '' : 'right-panel-active'}`}>
      <div className="form-container sign-up-container">
        <form action="#">
          <h3>Create Account</h3>
          {/* Rest of the code for the signup form */}
          <input type="text" placeholder="Name" />
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button>Sign Up</button>
        </form>
      </div>
      <div className="form-container sign-in-container">
        <form action="#">
          <h1>Sign in</h1>
          
          <span>or use your account</span>
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <a href="#">Forgot your password?</a>
          <button>Sign In</button>
        </form>
      </div>
      <div className="overlay-container">
        <div className="overlay">
          <div className={`overlay-panel ${isSignUp ? 'overlay-right' : 'overlay-left'}`}>
            {isSignUp ? (
              <>
                <h1>Hello, Friend!</h1>
                <p>Enter your personal details and start the journey with us</p>
                <button className="ghost" onClick={handleSignInClick}>Sign Up</button>
              </>
            ) : (
              <>
                <h1>Welcome Back!</h1>
                <p>To keep connected with us, please login with your personal info</p>
                <button className="ghost" onClick={handleSignUpClick}>Sign In</button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
