import React, { useState } from "react";
import { Container, Paper, Avatar, Typography, TextField, Button, MenuItem } from "@mui/material";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import { styled } from "@mui/system"; // Import styled from @mui/system

const BlurryBackground = styled("div")`
  background: url('background-image.jpg'); // Replace with your background image
  background-size: cover;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledPaper = styled(Paper)`
  padding: ${({ theme }) => theme.spacing(4)}; // Access theme spacing
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(255, 255, 255, 0.8);
  border-radius: 8px;
  backdrop-filter: blur(10px);
`;

const StyledAvatar = styled(Avatar)`
  background-color: ${({ theme }) => theme.palette.primary.main}; // Access theme palette
`;

const SignupPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");

  const handleSignup = () => {
    // Handle signup logic here
  };

  return (
    <BlurryBackground>
      <Container maxWidth="sm">
        <StyledPaper elevation={3}>
          <StyledAvatar>
            <LockOutlinedIcon />
          </StyledAvatar>
          <Typography variant="h5">Sign Up</Typography>
          <TextField
            label="Email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            label="Password"
            fullWidth
            margin="normal"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <TextField
            label="Role"
            fullWidth
            margin="normal"
            select
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <MenuItem value="Manager">Manager</MenuItem>
            <MenuItem value="Developer">Developer</MenuItem>
            <MenuItem value="QA">QA</MenuItem>
          </TextField>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleSignup}
            style={{ marginTop: "20px" }}
          >
            Sign Up
          </Button>
        </StyledPaper>
      </Container>
    </BlurryBackground>
  );
};

export default SignupPage;
