import { useState } from "react";
import Logo from "../../Images/Logo1.png";
import image from "../../Images/Logo2.png"
import axios from "axios";
import React from "react";
axios.defaults.baseURL = "http://localhost:5000";
function Login() {
  const [isSignUp, setIsSignUp] = useState(true);
  const [selectedRole, setSelectedRole] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSignUpClick = () => {
    setIsSignUp(true);
  };

  const handleSignInClick = () => {
    setIsSignUp(false);
  };

  const handleRoleChange = (event: {
    target: { value: React.SetStateAction<string> };
  }) => {
    setSelectedRole(event.target.value);
  };
  const handleSignUp = async () => {
    try {
      const response = await axios.post("/api/user/postItem", {
        name,
        email,
        password,
        user_type: selectedRole,
      });

      if (response.status === 201) {
        setName("");
        setEmail("");
        setPassword("");
        setSelectedRole("manager");
        setError("");
      }
    } catch (error: any) {
      if (error.response && error.response.data) {
        setError(error.response.data.message);
      } else {
        setError("An error occurred while registering.");
      }
    }
  };
  return (
    <div className={`container ${isSignUp ? "" : "right-panel-active"}`}>
      <div className="form-container sign-up-container">
        <form action="#">
          <img src={Logo} alt="" className="logo" />
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label htmlFor="role">Select Role:</label>
          <select id="role" value={selectedRole} onChange={handleRoleChange}>
            <option value="" disabled>
              Select Role
            </option>
            <option value="manager">Manager</option>
            <option value="developer">Developer</option>
            <option value="qa">QA</option>
          </select>
          {error && <p className="error-message">{error}</p>}
          <button onClick={handleSignUp}>Sign Up</button>
        </form>
      </div>
      <div className="form-container sign-in-container">
        <form action="#">
          <img src={Logo} alt="" className="logo" />
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button>Sign In</button>
        </form>
      </div>
      <div className="overlay-container">
        <div className="overlay">
          <div
            className={`overlay-panel ${
              isSignUp ? "overlay-right" : "overlay-left"
            }`}
          >
            {isSignUp ? (
              <>
                <div className="right-div">
                  <h1>Hello</h1>
                  <img className="bug-image" src={image} alt="Bug" />
                </div>
                <p>Enter your personal details and start the journey with us</p>
                <button className="ghost" onClick={handleSignInClick}>
                  Sign Up
                </button>
              </>
            ) : (
              <>
                <h2>Welcome Back!</h2>
                <p>
                  Already have an account? & To keep connected with us, please
                  login with your personal data
                </p>
                <button className="ghost" onClick={handleSignUpClick}>
                  Sign In
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
