// src/App.js

import 'bootstrap/dist/css/bootstrap.min.css';
import Login from './components/Auth/Login';
import SignUp from './components/Auth/Signup';
import './App.css';

function App() {
  const [isSignUp, setIsSignUp] = useState(false);

  const handleButtonClick = () => {
    setIsSignUp(!isSignUp); // Toggle between login and signup forms
  };

  return (
    <div className="App">
      {isSignUp ? <SignUp /> : <Login />}
      <div className={`overlay-container ${isSignUp ? 'right-panel-active' : ''}`}>
        <div className="overlay">
          <div className={`overlay-panel ${isSignUp ? 'overlay-right' : 'overlay-left'}`}>
            {isSignUp ? (
              <>
                <h1>Hello, Friend!</h1>
                <p>Enter your personal details and start the journey with us</p>
                <button className="ghost" onClick={handleButtonClick}>Sign In</button>
              </>
            ) : (
              <>
                <h1>Welcome Back!</h1>
                <p>To keep connected with us, please login with your personal info</p>
                <button className="ghost" onClick={handleButtonClick}>Sign Up</button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
